let WIDTH = 0;
let HEIGHT = 0;
var Player = function (p, pos) {
  this.w = HEIGHT * 0.05;
  this.h = HEIGHT * 0.18;
  this.score = 0;
  let tmp = this.w * 2;
  !pos ? tmp : tmp = WIDTH - tmp;
  this.pos = p.createVector(tmp, HEIGHT / 2 - this.h / 2);
  this.acc = p.createVector(0, 0);
  this.spd = 10;
  this.maxSpd = 10;
  this.show = function () {
    p.noStroke();
    p.fill(255);
    p.rect(this.pos.x, this.pos.y, this.w, this.h);
  };
  this.update = function () {
    this.acc.y = p.constrain(this.acc.y, -this.maxSpd, this.maxSpd);
    this.pos.add(this.acc);
    this.pos.y = p.constrain(this.pos.y, 0, HEIGHT - this.h);
  };
  this.up = function () {
    this.acc.y -= this.spd;
  };
  this.down = function () {
    this.acc.y += this.spd;
  };
  this.stop = function () {
    this.acc.y = 0;
  };
};
var Ball = function (p) {
  this.x = WIDTH / 2;
  this.y = HEIGHT / 2;
  this.size = WIDTH * 0.04;
  this.velocity = 4;
  this.dx = this.velocity;
  this.dy = this.velocity;
  this.show = function () {
    p.fill(200, 10, 200);
    p.rect(this.x, this.y, this.size, this.size);
  };
  this.update = function (player) {
    if (this.y > player.y && this.y < player.y + player.h) this.dx = this.dx * -1;
    if (this.y <= 0 || this.y + this.size > HEIGHT) this.dy = this.dy * -1;
    this.x = this.x + this.dx;
    this.y = this.y + this.dy;
  };
  this.bouncex = function () {
    this.dx = this.dx * -1;
    this.velocity += 0.03;
  };
  this.bouncey = function () {
    this.dy = this.dy * -1;
    this.velocity += 0.03;
  };
  this.respawn = function () {
    this.x = WIDTH / 2;
    this.y = HEIGHT / 2;
  };
};
var sketch = function (p) {
  let player;
  let player2;
  let ball;
  function colide(iplayer, iball) {
    let px = iplayer.pos.x;
    let py = iplayer.pos.y;
