      p;
      iball.bouncex();
    }
  }
  p.preload = function () {};
  p.setup = function () {
    WIDTH = p.windowWidth - 20;
    HEIGHT = p.windowHeight - 20;
    p.createCanvas(WIDTH, HEIGHT);
    player = new Player(p, 0);
    player2 = new Player(p, 1);
    ball = new Ball(p);
  };
  p.windowResized = function () {
    ball.size = WIDTH * 0.04;
    WIDTH = p.windowWidth - 20;
    HEIGHT = p.windowHeight - 20;
    player.w = p.resizeCanvas(WIDTH, HEIGHT);
  };
  p.draw = function () {
    p.background(0);
    p.noStroke();
    p.fill(255);
    p.rect(WIDTH / 2, 0, WIDTH * 0.02, HEIGHT);
    player.update();
    player2.update();
    ball.update(player);
    if (ball.x < 0) {
      player2.score += 1;
      ball.respawn();
    }
    if (ball.x > WIDTH) {
      player.score += 1;
      ball.respawn();
    }
    p.textSize(40);
    p.text(player.score, WIDTH * 0.25, HEIGHT * 0.25);
    p.text(player2.score, WIDTH * 0.75, HEIGHT * 0.25);
    colide(player, ball);
    colide(player2, ball);
    player.show();
